Code class Solution { public: int minRemoval(vector&lt;int&gt;&amp; nums, int k) { sort(nums.begin(), nums.end()); int n = nums.size(); int j = 0, ans = n; for (int i = 0; i &lt; n; i++) { while (j &lt; n &amp;&amp; nums[j] &lt;= 1LL*nums[i] * k) j++; ans = min(ans, n-j+i); } return ans; } };
